function writeNow() {
    const now = new Date()
    const weekDay = ['日', '一', '二', '三', '四', '五', '六']
    const time= `今天是: ${now.getFullYear()}年${now.getMonth() + 1}月${now.getDate()}号 星期${
      weekDay[now.getDay()]
    }`
    const fs=require('fs')
    fs.writeFile('./file/timeString.txt', time, 'utf-8', err => {
        if (err) return console.log('写入失败：' + err)
        console.log(time + ' 写入成功')
      })
  }
  module.exports.writeNow = writeNow